import "core-js/modules/es.array.push.js";
export default {
  name: "RegistrationPage",
  data() {
    return {
      username: "",
      password: "",
      email: "",
      errorMessage: "",
      isValid: false,
      usernameRules: [v => !!v || "Username is required", v => v && v.length >= 3 || "Username must be at least 3 characters"],
      passwordRules: [v => !!v || "Password is required", v => v && v.length >= 6 || "Password must be at least 6 characters"],
      emailRules: [v => !!v || "Email is required", v => /.+@.+\..+/.test(v) || "Enter a valid email"]
    };
  },
  methods: {
    async register() {
      this.errorMessage = "";
      const isFormValid = await this.$refs.registerForm.validate();
      if (!isFormValid) {
        this.errorMessage = "Bitte alle Felder korrekt ausfüllen!";
        return;
      }
      try {
        const response = await fetch("http://localhost:8080/api/register", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            username: this.username,
            email: this.email,
            password: this.password
          })
        });
        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || "Registrierung fehlgeschlagen");
        }
        alert("Registrierung erfolgreich!");
        this.$router.push("/home");
      } catch (error) {
        this.errorMessage = error.message;
      }
    }
  }
};
